package jjgg.academysystem.entities;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;@Entity@Table(name = "user_rol")public class UserRol {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @ManyToOne(fetch = FetchType.EAGER)    @JoinColumn(name = "user_id")    @JsonIgnore    private User user;    @ManyToOne    @JoinColumn(name = "rol_id")    @JsonIgnore    private Rol rol;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public User getUser() {        return user;    }    public void setUser(User user) {        this.user = user;    }    public Rol getRol() {        return rol;    }    public void setRol(Rol rol) {        this.rol = rol;    }}